AND A.CUST_NM LIKE '%' + #{searchCustNm} + '%'
SELECT A.CUST_ID , A.SUB_TASK_NO , A.CUST_EM , A.CUST_NM , A.SEND_DT , A.DENIED_TYPE , A.SEND_RCODE , '' AS OPEN_DT , A.SEND_MSG , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SENDLOG A WHERE A.TASK_NO = #{taskNo} AND A.SUB_TASK_NO = #{subTaskNo} AND A.SEND_RCODE = '000' AND NOT EXISTS ( SELECT '1' FROM NEO_RESPLOG B WHERE B.TASK_NO = A.TASK_NO AND B.SUB_TASK_NO = A.SUB_TASK_NO AND B.CUST_ID = A.CUST_ID )
