AND A.CUST_ID LIKE '%' + #{searchCustId} + '%'
AND A.CUST_EM = #{searchCustEm}
AND A.CUST_NM LIKE '%' + #{searchCustNm} + '%'
SELECT A.CUST_ID , A.CUST_EM , A.CUST_NM , A.SEND_RCODE , A.SEND_DT , A.SEND_MSG , A.DENIED_TYPE , '' AS OPEN_DT , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, SEND_RCODE, RETRY_CNT, SEND_DT, SEND_MSG, DENIED_TYPE FROM NEO_SENDLOG WHERE TASK_NO = #{taskNo} AND SUB_TASK_NO = #{subTaskNo} AND RETRY_CNT = MAX_RETRY_CNT ) A, ( SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, MAX(RETRY_CNT) AS MAX_RETRY_CNT FROM NEO_SENDLOG WHERE TASK_NO = #{taskNo} AND SUB_TASK_NO = #{subTaskNo} AND RETRY_CNT = MAX_RETRY_CNT GROUP BY SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM ) B WHERE A.SUB_TASK_NO = B.SUB_TASK_NO AND A.CUST_ID = B.CUST_ID AND A.CUST_EM = B.CUST_EM AND A.CUST_NM = B.CUST_NM AND A.RETRY_CNT = B.MAX_RETRY_CNT AND SEND_RCODE != '000'
AND A.CUST_ID LIKE '%' + #{searchCustId} + '%'
AND A.CUST_EM = #{searchCustEm}
AND A.CUST_NM LIKE '%' + #{searchCustNm} + '%'
