SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT ISNULL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , ISNULL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
( NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_RESPLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) A, ( SELECT ISNULL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, ISNULL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
( NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_LINKLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) C

SELECT * FROM ( SELECT P.TASK_NO , Q.SUB_TASK_NO , Q.SEND_DT , Q.WORK_STATUS , R.CD_NM WORK_STATUS_NM , P.TASK_NM , P.CHANNEL , P.SEND_REPEAT , P.STATUS , P.SEG_NO , P.SEND_TERM_END_DT , P.SEND_TERM_LOOP , P.REG_DT , P.CONT_FL_PATH , P.SURVEYNO , P.SEG_NM , P.USER_NM , P.CAMP_NM , P.CHANNEL_NM , P.SEND_REPEAT_NM , P.STATUS_NM , ROW_NUMBER() OVER (ORDER BY Q.SEND_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT A.TASK_NO , A.TASK_NM , A.CHANNEL , A.SEND_REPEAT , A.STATUS , A.SEG_NO , A.SEND_TERM_END_DT , A.SEND_TERM_LOOP , A.REG_DT , A.CONT_FL_PATH , A.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , (SELECT MAX(SUB_TASK_NO) SUB_TASK_NO FROM NEO_SUBTASK WHERE TASK_NO = A.TASK_NO AND WORK_STATUS NOT IN ('000', '001', '002', '201', '202', '203', '204')
AND SEND_DT >= #{searchStartDt} + '000000'
AND SEND_DT < #{searchEndDt} +'240000'
) SUB_TASK_NO FROM NEO_TASK A , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = #{uilang}) F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang}) G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C023' AND UILANG = #{uilang}) H WHERE A.SEG_NO = C.SEG_NO AND A.USER_ID = D.USER_ID AND A.CAMP_NO = E.CAMP_NO AND A.CHANNEL = F.CD AND A.SEND_REPEAT = G.CD AND A.STATUS = H.CD
AND A.SEND_REPEAT = #{searchSendRepeat}
AND UPPER(A.TASK_NM) LIKE '%' + #{searchTaskNm} + '%'
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.CAMP_NO = #{searchCampNo}
AND A.SEG_NO = #{searchSegNo}
) P , NEO_SUBTASK Q , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = #{uilang}) R WHERE P.TASK_NO = Q.TASK_NO AND P.SUB_TASK_NO = Q.SUB_TASK_NO AND Q.WORK_STATUS NOT IN ('000', '001', '002', '201', '202', '203', '204') ANd Q.SEND_TEST_YN ='N' AND Q.WORK_STATUS = R.CD ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT A.SUB_TASK_NO , A.SEND_DT , A.END_DT , A.WORK_STATUS , B.TASK_NO , B.TASK_NM , B.CHANNEL , B.SEND_REPEAT , B.STATUS , B.SEG_NO , B.SEND_TERM_END_DT , B.SEND_TERM_LOOP , B.REG_DT , B.CONT_FL_PATH , B.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC, A.TASK_NO DESC, A.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SUBTASK A , NEO_TASK B , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = #{uilang}) F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang}) G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = #{uilang}) H WHERE A.SEND_TEST_YN != 'Y' AND A.WORK_STATUS IN ('002','003') AND A.TASK_NO = B.TASK_NO AND B.SEG_NO = C.SEG_NO AND B.USER_ID = D.USER_ID AND B.CAMP_NO = E.CAMP_NO AND B.CHANNEL = F.CD AND B.SEND_REPEAT = G.CD AND A.WORK_STATUS = H.CD AND B.SEND_REPEAT = '001'
AND UPPER(B.TASK_NM) LIKE '%' + #{searchTaskNm} + '%'
AND B.DEPT_NO = #{searchDeptNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
AND B.CAMP_NO = #{searchCampNo}
AND A.SEND_DT >= #{searchStartDt} + '000000'
AND A.SEND_DT < #{searchEndDt} +'240000'
AND B.TASK_NO = #{taskNo}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT NEO_AR_SENDLOG.TASK_NO , NEO_TASK.TASK_NM , (SELECT CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang} AND CD = NEO_TASK.SEND_REPEAT) SEND_REPEAT_NM , NEO_SEGMENT.SEG_NM , NEO_CAMPAIGN.CAMP_NM , ISNULL(SUM(SEND_AR_AMT),0) AS TOT_CNT , ISNULL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_TASK , NEO_SUBTASK , NEO_SEGMENT , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.TASK_NO = NEO_TASK.TASK_NO AND NEO_AR_SENDLOG.TASK_NO = NEO_SUBTASK.TASK_NO AND NEO_AR_SENDLOG.SUB_TASK_NO = NEO_SUBTASK.SUB_TASK_NO AND NEO_TASK.SEG_NO = NEO_SEGMENT.SEG_NO AND NEO_TASK.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND NEO_SUBTASK.WORK_STATUS NOT IN ('000','001','002','201','202','203','204' ) AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY NEO_AR_SENDLOG.TASK_NO, NEO_TASK.TASK_NM, NEO_SEGMENT.SEG_NM, NEO_CAMPAIGN.CAMP_NM, NEO_TASK.SEND_REPEAT ORDER BY NEO_AR_SENDLOG.TASK_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT ISNULL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , ISNULL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo}
) A, ( SELECT ISNULL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, ISNULL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo}
) C

SELECT A.STEP1 , A.STEP2 , ISNULL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A LEFT OUTER JOIN ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B ON (A.STEP1 = B.RCODE_STEP1 AND A.STEP2 = B.RCODE_STEP2) ORDER BY A.STEP1, A.STEP2

SELECT NEO_AR_SENDLOG.CAMP_NO , NEO_CAMPAIGN.CAMP_NM , ISNULL(SUM(SEND_AR_AMT),0) AS TOT_CNT , ISNULL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY NEO_AR_SENDLOG.CAMP_NO, NEO_CAMPAIGN.CAMP_NM ORDER BY NEO_AR_SENDLOG.CAMP_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT ISNULL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , ISNULL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE 1=1 AND
NEO_AR_RESPLOG.CAMP_NO = #{campNo}
) A, ( SELECT ISNULL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT , ISNULL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE 1=1 AND
NEO_AR_LINKLOG.CAMP_NO = #{campNo}
) C

SELECT A.STEP1 , A.STEP2 , ISNULL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A LEFT OUTER JOIN ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B ON (A.STEP1 = B.RCODE_STEP1 AND A.STEP2 = B.RCODE_STEP2) ORDER BY A.STEP1, A.STEP2

SELECT AA.YMD , SEND_CNT , ISNULL(SUCC_CNT,0) SUCC_CNT , ISNULL(OPEN_CNT,0) OPEN_CNT , ISNULL(VALID_CNT,0) VALID_CNT , ISNULL(CLICK_CNT,0) CLICK_CNT , ISNULL(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT SUBSTRING(A.SEND_DT,1,6) YMD , ISNULL(SUM(SEND_AR_AMT),0) SEND_CNT, ISNULL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} + '0000' AND A.SEND_DT < #{searchEndDt}+ '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY SUBSTRING(A.SEND_DT,1,6) ) AA LEFT OUTER JOIN ( SELECT SUBSTRING(A.OPEN_DT,1,6) YMD , ISNULL(SUM(RESP_AR_AMT),0) OPEN_CNT, ISNULL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} + '0000' AND A.OPEN_DT < #{searchEndDt} + '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY SUBSTRING(A.OPEN_DT,1,6) ) BB ON (AA.YMD = BB.YMD) LEFT OUTER JOIN ( SELECT SUBSTRING(A.CLICK_DT,1,6) YMD , ISNULL(SUM(CLICK_AR_AMT),0) CLICK_CNT, ISNULL(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} + '0000' AND A.CLICK_DT < #{searchEndDt}+ '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY SUBSTRING(A.CLICK_DT,1,6) ) DD ON (AA.YMD = DD.YMD) ORDER BY AA.YMD

SELECT EE.WEEK_NM AS WEEK_NM , ISNULL(SEND_CNT,0) SEND_CNT , ISNULL(SUCC_CNT,0) SUCC_CNT , ISNULL(OPEN_CNT,0) OPEN_CNT , ISNULL(VALID_CNT,0) VALID_CNT , ISNULL(CLICK_CNT,0) CLICK_CNT , ISNULL(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT CD, CD_NM AS WEEK_NM FROM NEO_CD WHERE CD_GRP = 'C005' AND UILANG = #{uilang} ) EE LEFT OUTER JOIN ( SELECT DAYOFWEEK, ISNULL(SUM(SEND_AR_AMT),0) SEND_CNT , ISNULL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} + '0000' AND A.SEND_DT <= #{searchEndDt} + '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) AA ON (EE.CD = AA.DAYOFWEEK) LEFT OUTER JOIN ( SELECT DAYOFWEEK, ISNULL(SUM(RESP_AR_AMT),0) OPEN_CNT , ISNULL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} + '0000' AND A.OPEN_DT <= #{searchEndDt} + '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) BB ON (EE.CD = BB.DAYOFWEEK) LEFT OUTER JOIN ( SELECT DAYOFWEEK, ISNULL(SUM(CLICK_AR_AMT),0) CLICK_CNT, ISNULL(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} + '0000' AND A.CLICK_DT <= #{searchEndDt} + '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) DD ON (EE.CD = DD.DAYOFWEEK) ORDER BY EE.CD

