SELECT * FROM ( SELECT T1.SEND_DT , T3.MAIL_FROM_NM , T3.MAIL_FROM_EM , T1.DEPT_NO , T4.DEPT_NM , T1.USER_ID , T5.USER_NM , T3.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP= 'C017' AND CD=T3.SEND_REPEAT) SEND_REPEAT_NM , T1.TASK_NO , T1.SUB_TASK_NO , T3.TASK_NM , T3.MAIL_TITLE , T1.CUST_ID , T1.CAMP_NO , T6.CAMP_NM , T6.CAMP_TY , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP= 'C004' AND CD=T6.CAMP_TY) CAMP_TY_NM , T1.TARGET_GRP_TY , T1.CUST_EM , T1.CUST_NM , T1.SEND_RCODE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP= 'C035' AND CD=T1.SEND_RCODE) SEND_RCODE_NM , T1.RCODE_STEP1 , T1.RCODE_STEP2 , T1.RCODE_STEP3 , T1.SEND_MSG , T1.SEND_AMT , T1.DOMAIN_CD , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP= 'C003' AND CD=T1.DOMAIN_CD) DOMAIN_NM , T1.CUST_DOMAIN , ROW_NUMBER() OVER (ORDER BY T1.SEND_DT DESC, T1.CUST_ID, T1.CUST_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SENDLOG T1 , NEO_SUBTASK T2 , NEO_TASK T3 , NEO_DEPT T4 , NEO_USER T5 , NEO_CAMPAIGN T6 WHERE T1.TASK_NO = T2.TASK_NO AND T1.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.TASK_NO = T3.TASK_NO AND T1.DEPT_NO = T4.DEPT_NO AND T1.USER_ID = T5.USER_ID AND T1.CAMP_NO = T6.CAMP_NO
AND T1.SEND_DT >= #{searchStartDt} + '000000'
AND T1.SEND_DT < #{searchEndDt} + '240000'
AND T2.WORK_STATUS NOT IN ('000', '001', '002', '003', '201', '202', '203')
AND T2.WORK_STATUS = #{searchWorkStatus}
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.USER_ID = #{searchUserId}
AND T5.USER_NM LIKE '%'+ #{searchUserNm} + '%'
AND T3.SEND_REPEAT = #{searchSendRepeat}
AND T3.SEG_NO = #{searchSegNo}
) TTT WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT T1.SEND_DT , T3.MAIL_FROM_NM , T3.MAIL_FROM_EM , T1.USER_ID , T5.USER_NM , T3.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD=T3.SEND_REPEAT) SEND_REPEAT_NM , T1.TASK_NO , T1.SUB_TASK_NO , T3.TASK_NM , T3.MAIL_TITLE , T1.CUST_ID , T1.CAMP_NO , T6.CAMP_NM , T1.CUST_EM , T1.CUST_NM , T1.SEND_RCODE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C035' AND CD=T1.SEND_RCODE) SEND_RCODE_NM , T3.ATT_CNT , '10' SERVICE_GB , T3.CONT_FL_PATH , ISNULL(T7.SOURCE_URL, '') WEB_AGENT , ISNULL(T7.SECU_ATT_TYP, '') WEB_AGENT_TYP , ISNULL(T8.RESP_AMT, 0) RESP_AMT , ISNULL(T8.OPEN_DT, '') OPEN_DT , T1.BIZKEY , ISNULL(T2.RTY_TASK_NO,0 ) RTY_NO , (SELECT COUNT(*) FROM NEO_SEGMENT_REAL WHERE SEG_NO = T3.SEG_NO) SEG_REAL , ROW_NUMBER() OVER (ORDER BY T1.SEND_DT DESC, T1.CUST_ID, T1.CUST_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SENDLOG T1 INNER JOIN NEO_SUBTASK T2 ON (T1.TASK_NO = T2.TASK_NO AND T1.SUB_TASK_NO = T2.SUB_TASK_NO) INNER JOIN NEO_TASK T3 ON (T1.TASK_NO = T3.TASK_NO) INNER JOIN NEO_DEPT T4 ON (T1.DEPT_NO = T4.DEPT_NO) INNER JOIN NEO_USER T5 ON (T1.USER_ID = T5.USER_ID) INNER JOIN NEO_CAMPAIGN T6 ON (T1.CAMP_NO = T6.CAMP_NO) LEFT OUTER JOIN NEO_WEBAGENT T7 ON(T1.TASK_NO = T7.TASK_NO) LEFT OUTER JOIN NEO_RESPLOG T8 ON (T1.TASK_NO = T8.TASK_NO AND T1.SUB_TASK_NO = T8.SUB_TASK_NO AND T1.DEPT_NO = T8.DEPT_NO AND T1.CAMP_NO = T8.CAMP_NO AND T1.CUST_ID = T8.CUST_ID AND T1.USER_ID = T8.USER_ID ) WHERE 1=1
AND T1.SEND_DT >= #{searchStartDt} + '000000'
AND T1.SEND_DT < #{searchEndDt} + '240000'
AND T1.CAMP_NO = #{searchCampNo}
AND T1.CUST_ID LIKE '%' + #{searchCustId} + '%'
AND T1.CUST_NM LIKE '%' + #{searchCustNm} + '%'
AND T1.CUST_EM = #{searchCustEm}
AND T1.SEND_RCODE = '000'
AND T1.SEND_RCODE != '000'
AND T5.USER_NM LIKE '%' + #{searchUserNm} + '%'
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , ATTCH_CNT , SERVICE_GB , CONT_FL_PATH , WEB_AGENT , WEB_AGENT_TYP , RESP_AMT , OPEN_DT , BIZKEY , SEG_REAL , RTY_NO FROM ( SELECT CONVERT(Y.SDATE, 'YYYYMMDDHH24MI') SEND_DT , X.SNAME MAIL_FROM_NM , X.SMAIL MAIL_FROM_EM , X.SID USER_ID , X.SNAME USER_NM , '099' SEND_REPEAT , '실시간' SEND_REPEAT_NM , X.TID TASK_NO , X.MID SUB_TASK_NO , C.TNM TASK_NM , Y.SUBJECT MAIL_TITLE , X.RID CUST_ID , X.TID CAMP_NO , C.TNM CAMP_NM , X.RMAIL CUST_EM , X.RNAME CUST_NM , X.RCODE SEND_RCODE , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG = #{uilang} AND X.RCODE = RCODE ) SEND_RCODE_NM , C.ATTCH_CNT , '20' SERVICE_GB , C.CONTENTS_PATH CONT_FL_PATH , (SELECT ISNULL(SOURCE_URL, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT , (SELECT ISNULL(SECU_ATT_TYP, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT_TYP , ISNULL(L.RSID, 0 ) RESP_AMT , REPLACE(REPLACE(REPLACE(ISNULL(L.RSDATE, ''), '/', ''), ':', ''), ' ' ,'') OPEN_DT , X.BIZKEY , 0 SEG_REAL , ISNULL(Y.RTY_MID, 0) RTY_NO FROM TS_RESULTLOG X INNER JOIN TS_SERVICETYP C ON (X.TID = C.TID) INNER JOIN TS_MAILQUEUE Y ON (X.MID = Y.MID AND X.SUBID= Y.SUBID AND X.TID = Y.TID ) INNER JOIN (SELECT MID, MAX(SUBID) SUBID FROM TS_MAILQUEUE GROUP BY MID) D ON ( X.MID = D.MID) LEFT OUTER JOIN TS_WEBAGENT T ON ( X.TID = T.TID) LEFT OUTER JOIN TS_RESPONSELOG L ON ( X.MID = L.MID AND X.SUBID = L.SUBID AND X.RID = L.RID) WHERE 1=1
AND X.STIME >= CONVERT(DATETIME( #{searchStartDt} , 'YYYYMMDD'), 'YYYY/MM/DD HH24:MI:SS')
AND X.STIME < CONVERT(DATETIME(#{searchEndDt}, 'YYYYMMDD')+1, 'YYYY/MM/DD HH24:MI:SS')
AND X.TID = #{searchCampNo}
AND X.RID LIKE '%' + #{searchCustId} + '%'
AND X.RNAME LIKE '%' + #{searchCustNm} + '%'
AND X.RMAIL = #{searchCustEm}
AND CONVERT(numeric, X.RCODE) = 1
AND CONVERT(numeric, X.RCODE) > 1
AND X.SNAME LIKE '%' + #{searchUserNm} + '%'
) RR WHERE NOT EXISTS (SELECT MID FROM TS_MAILQUEUE_TEST RT WHERE RR.SUB_TASK_NO = RT.MID) ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , ATT_CNT , SERVICE_GB , CONT_FL_PATH , WEB_AGENT FROM ( SELECT T1.SEND_DT , T3.MAIL_FROM_NM , T3.MAIL_FROM_EM , T1.USER_ID , T5.USER_NM , T3.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP= 'C017' AND CD=T3.SEND_REPEAT) SEND_REPEAT_NM , T1.TASK_NO , T1.SUB_TASK_NO , T3.TASK_NM , T3.MAIL_TITLE , T1.CUST_ID , T1.CAMP_NO , T6.CAMP_NM , T1.CUST_EM , T1.CUST_NM , T1.SEND_RCODE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C035' AND CD=T1.SEND_RCODE) SEND_RCODE_NM , T3.ATT_CNT , '10' SERVICE_GB , T3.CONT_FL_PATH , (SELECT ISNULL(SOURCE_URL,'') FROM NEO_WEBAGENT WHERE TASK_NO = T1.TASK_NO) WEB_AGENT FROM NEO_SENDLOG T1 , NEO_SUBTASK T2 , NEO_TASK T3 , NEO_DEPT T4 , NEO_USER T5 , NEO_CAMPAIGN T6 WHERE T1.TASK_NO = T2.TASK_NO AND T1.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.TASK_NO = T3.TASK_NO AND T1.DEPT_NO = T4.DEPT_NO AND T1.USER_ID = T5.USER_ID AND T1.CAMP_NO = T6.CAMP_NO
AND T1.SEND_DT >= #{searchStartDt} + '000000'
AND T1.SEND_DT < #{searchEndDt} + '240000'
AND T1.CAMP_NO = #{searchCampNo}
AND T1.CUST_ID LIKE '%' + #{searchCustId} + '%'
AND T1.CUST_NM LIKE '%' + #{searchCustNm} + '%'
AND T1.CUST_EM = #{searchCustEm}
AND T1.SEND_RCODE = '000'
AND T1.SEND_RCODE != '000'
AND T5.USER_NM LIKE '%' + #{searchUserNm} + '%'
UNION ALL SELECT REPLACE(REPLACE(REPLACE(CONVERT(VARCHAR(19), CONVERT(DATETIME,Y.SDATE, 112),126),'-',''),'T',''),':','') SEND_DT , X.SNAME MAIL_FROM_NM , X.SMAIL MAIL_FROM_EM , X.SID USER_ID , X.SNAME USER_NM , '099' SEND_REPEAT , '실시간' SEND_REPEAT_NM , X.TID TASK_NO , X.MID SUB_TASK_NO , C.TNM TASK_NM , Y.SUBJECT MAIL_TITLE , X.RID CUST_ID , X.TID CAMP_NO , C.TNM CAMP_NM , X.RMAIL CUST_EM , X.RNAME CUST_NM , X.RCODE SEND_RCODE , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG = #{uilang} AND X.RCODE = RCODE ) SEND_RCODE_NM , C.ATTCH_CNT , '20' SERVICE_GB , (SELECT ISNULL(SOURCE_URL,'') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT , C.CONTENTS_PATH CONT_FL_PATH FROM TS_RESULTLOG X , TS_SERVICETYP C , TS_MAILQUEUE Y , (SELECT MID, MAX(SUBID) SUBID FROM TS_MAILQUEUE GROUP BY MID) D WHERE X.TID = C.TID AND X.MID = Y.MID AND X.MID = D.MID AND X.SUBID= Y.SUBID AND X.TID = Y.TID
AND CONVERT(Y.SDATE, 'YYYYMMDD') >= #{searchStartDt}
AND CONVERT(Y.SDATE, 'YYYYMMDD') <= #{searchEndDt}
AND X.TID = #{searchCampNo}
AND X.RID LIKE '%' + #{searchCustId} + '%'
AND X.RNAME LIKE '%' + #{searchCustNm} + '%'
AND X.RMAIL = #{searchCustEm}
AND CONVERT(numeric, X.RCODE) = 1
AND CONVERT(numeric, X.RCODE) > 1
AND X.SNAME LIKE '%' + #{searchUserNm} + '%'
) SRC ORDER BY SEND_DT DESC

SELECT * FROM ( SELECT T1.CAMP_NO , T4.CAMP_NM , T1.SUB_TASK_CNT AS TASK_COUNT , T1.TASK_NO , T2.TASK_NM , T2.SEND_REPEAT , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP='C017' AND CD=T2.SEND_REPEAT) SEND_REPEAT_NM , (SELECT MAX(USER_NM) FROM NEO_USER WHERE USER_ID = T2.USER_ID) USER_NM , T2.DEPT_NO , T4.REG_DT AS CAMP_REG_DT , T2.REG_DT AS TASK_REG_DT , T5.SEND_DT , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP='C101' AND CD=T5.WORK_STATUS) WORK_STATUS_NM , ROW_NUMBER() OVER (ORDER BY T5.SEND_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT NT1.CAMP_NO , CONVERT(FLOAT ,LEFT(MAX(NS1.SEND_DT + FORMAT(NT1.TASK_NO , '00000000000000000009')), 13)) AS TASK_NO , CONVERT(FLOAT ,LEFT(MAX(NS1.SEND_DT + FORMAT(NT1.TASK_NO , '00000000000000000009') + FORMAT(NS1.SUB_TASK_NO , '00000000000000000009')), 33)) AS SUB_TASK_NO , COUNT(*) SUB_TASK_CNT FROM NEO_TASK NT1 , NEO_SUBTASK NS1 WHERE NT1.TASK_NO = NS1.TASK_NO AND NS1.WORK_STATUS NOT IN ('000', '001', '002', '201','202','203','204') AND NS1.SEND_DT >= #{searchStartDt} + '000000' AND NS1.SEND_DT < #{searchEndDt} + '240000' AND NS1.SEND_TEST_YN = 'N' AND NT1.STATUS != '002'
AND NT1.CAMP_NO = #{searchCampNo}
AND NT1.USER_ID LIKE '%' + #{searchUserId} + '%'
AND NT1.DEPT_NO = #{searchDeptNo}
GROUP BY NT1.CAMP_NO ) T1 INNER JOIN NEO_TASK T2 ON (T1.TASK_NO = T2.TASK_NO ) INNER JOIN NEO_CAMPAIGN T4 ON (T1.CAMP_NO = T4.CAMP_NO ) INNER JOIN NEO_SUBTASK T5 ON (T1.TASK_NO = T5.TASK_NO AND T1.SUB_TASK_NO = T5.SUB_TASK_NO) ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT T2.TID CAMP_NO , T2.TNM CAMP_NM , DATE_FORMAT(T3.SDATE, '%Y%m%d%H%i') SEND_DT , T1.TASK_COUNT , T2.EMAIL_SUBJECT TASK_NM , '실시간' SEND_REPEAT_NM , T3.SID USER_ID , ISNULL((SELECT USER_NM FROM NEO_USER WHERE USER_ID = T3.SID), (SELECT USER_NM FROM NEO_USER WHERE USER_ID = T2.SID)) USER_NM , ISNULL(T3.DEPT_NO, T2.DEPT_NO) DEPT_NO , T2.REG_DT CAMP_REG_DT , DATE_FORMAT( T3.CDATE, '%Y%m%d%H%i%s') TASK_REG_DT , (SELECT CD_DESC FROM TS_RCODE WHERE RCODE = T4.RCODE) WORK_STATUS_NM , ROW_NUMBER() OVER (ORDER BY TT3.SDATE DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT C.TID , MAX(Y.MID) MID, COUNT(*) TASK_COUNT FROM TS_SERVICETYP C, TS_MAILQUEUE Y WHERE C.TID = Y.TID AND ISNULL(Y.STATUS , 1 ) > 0 AND NOT EXISTS (SELECT AA.MID FROM TS_MAILQUEUE_TEST AA WHERE AA.MID = Y.MID )
AND Y.SDATE >= DATETIME(#{searchStartDt} + '000000' , 'YYYYMMDDHH24MISS')
AND Y.SDATE <= DATETIME(#{searchEndDt} + '235959' , 'YYYYMMDDHH24MISS')
AND C.TID = #{searchCampNo}
AND Y.USER_ID LIKE '%' + #{searchUserId} + '%'
AND Y.DEPT_NO = #{searchDeptNo}
GROUP BY C.TID ) T1 , TS_SERVICETYP T2 , TS_MAILQUEUE T3 , TS_RESULTLOG T4 WHERE T1.TID= T2.TID AND T1.MID = T3.MID AND T1.MID = T4.MID ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT T0.ORD_SEQ , T1.TASK_NO , T1.TASK_NM , T2.SUB_TASK_NO , T1.SEG_NO , T3.SEG_NM , T1.CAMP_NO , T4.CAMP_NM , T1.USER_ID , T1.MAIL_FROM_NM , T1.STATUS , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T2.WORK_STATUS , (SELECT MIN(SEND_DT) FROM NEO_SUBTASK WHERE TASK_NO = T1.TASK_NO AND WORK_STATUS NOT IN ('000','001','002','201','202','203', '204') AND SEND_TEST_YN = 'N') SEND_TERM_START_DT , T1.SEND_TERM_END_DT , T6.CD_NM WORK_STATUS_NM , T6.CD_DTL WORK_STATUS_DTL , T2.SEND_DT , T2.END_DT , T1.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T1.SEND_REPEAT) SEND_REPEAT_NM , (SELECT COUNT(1) FROM NEO_SUBTASK WHERE TASK_NO = T1.TASK_NO) MAIL_CNT , ISNULL(T2.RTY_TASK_NO, 0 ) RTY_NO , T1.REG_DT TASK_REG_DT , ISNULL(T1.SEND_TERM_LOOP, 0 ) SEND_TERM_LOOP , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C019' AND CD = T1.SEND_TERM_LOOP_TY) SEND_TERM_LOOP_TY_NM , (SELECT COUNT(*) FROM NEO_SEGMENT_RETRY WHERE SEG_NO = T1.SEG_NO) SEG_RETRY , ROW_NUMBER() OVER (ORDER BY T0.ORD_SEQ DESC, T2.TASK_NO, T2.SUB_TASK_NO) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT NT1.CAMP_NO , NT1.TASK_NO AS TASK_NO , NS1.SUB_TASK_NO , NS2.ORD_SEQ , MAX(NS1.SEND_DT) OVER(PARTITION BY NT1.TASK_NO) AS ORD_SEQ FROM NEO_TASK NT1, NEO_SUBTASK NS1 WHERE NT1.TASK_NO = NS1.TASK_NO AND NT1.TASK_NO = NS2.TASK_NO AND NS1.WORK_STATUS NOT IN ('000', '001', '002', '201','202','203','204') AND NS1.SEND_DT >= #{searchStartDt} + '000000' AND NS1.SEND_DT < #{searchEndDt} + '240000' AND NS1.SEND_TEST_YN = 'N' AND NT1.STATUS != '002'
AND NT1.CAMP_NO = #{searchCampNo}
AND NT1.DEPT_NO = #{searchDeptNo}
AND NT1.DEPT_NO = #{searchDeptNo}
AND NT1.TASK_NM LIKE '%' + #{searchTaskNm} + '%'
AND NT1.SEND_REPEAT = #{searchSendRepeat}
AND NT1.USER_ID = #{searchUserId}
) T0, NEO_TASK T1 , NEO_SUBTASK T2 , NEO_SEGMENT T3 , NEO_CAMPAIGN T4 , NEO_USER T5 , NEO_CD T6 WHERE T0.TASK_NO = T1.TASK_NO AND T0.TASK_NO = T2.TASK_NO AND T0.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.SEG_NO = T3.SEG_NO AND T1.CAMP_NO = T4.CAMP_NO AND T1.USER_ID = T5.USER_ID AND T2.WORK_STATUS = T6.CD AND T6.UILANG = #{uilang} AND T6.CD_GRP = 'C101'
AND T2.WORK_STATUS NOT IN ('000','001','002','201','202','203','204')
AND T2.WORK_STATUS = #{searchWorkStatus}
AND T2.WORK_STATUS NOT IN ('000','001','002','003','201','202','203','204')
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , ATTCH_CNT , SERVICE_GB , CONT_FL_PATH , WEB_AGENT , WEB_AGENT_TYP , RESP_AMT , BIZKEY , RTY_NO , ROW_NUMBER() OVER (ORDER BY SEND_DT DESC, CUST_ID, CUST_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT CONVERT(Y.SDATE, 'YYYYMMDDHH24MI') SEND_DT , X.SNAME MAIL_FROM_NM , X.SMAIL MAIL_FROM_EM , X.SID USER_ID , X.SNAME USER_NM , '099' SEND_REPEAT , '실시간' SEND_REPEAT_NM , X.TID TASK_NO , X.MID SUB_TASK_NO , C.TNM TASK_NM , Y.SUBJECT MAIL_TITLE , X.RID CUST_ID , X.TID CAMP_NO , C.TNM CAMP_NM , X.RMAIL CUST_EM , X.RNAME CUST_NM , X.RCODE SEND_RCODE , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG = #{uilang} AND X.RCODE = RCODE ) SEND_RCODE_NM , C.ATTCH_CNT , '20' SERVICE_GB , C.CONTENTS_PATH CONT_FL_PATH , (SELECT ISNULL(SOURCE_URL, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT , (SELECT ISNULL(SECU_ATT_TYP, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT_TYP , ISNULL(L.RSID, 0 ) RESP_AMT , X.BIZKEY , ISNULL(Y.RTY_MID, 0 ) RTY_NO FROM TS_MAILQUEUE Y INNER JOIN TS_SERVICETYP C ON (Y.TID = C.TID) INNER JOIN TS_RESULTLOG X ON (X.MID = Y.MID AND X.SUBID= Y.SUBID) LEFT OUTER JOIN TS_WEBAGENT T ON (X.TID = T.TID) LEFT OUTER JOIN TS_RESPONSELOG L ON (X.MID = L.MID AND X.SUBID = L.SUBID AND X.RID = L.RID) WHERE Y.TID = #{searchCampNo} AND NOT EXISTS (SELECT AA.MID FROM TS_MAILQUEUE_TEST AA WHERE AA.MID = Y.MID ) AND ISNULL(Y.STATUS , 1 ) > 0
AND Y.SDATE >= DATETIME(#{searchStartDt} + '000000' , 'YYYYMMDDHH24MISS')
AND Y.SDATE <= DATETIME(#{searchEndDt} + '235959' , 'YYYYMMDDHH24MISS')
AND X.RID LIKE '%' + #{searchCustId} + '%'
AND X.RNAME LIKE '%' + #{searchCustNm} + '%'
AND X.RMAIL = #{searchCustEm}
AND X.RCODE = '1'
AND X.RCODE != '1'
AND X.SNAME LIKE '%' + #{searchUserNm} + '%'
) ) RR ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}
