mapper.select:
SELECT * FROM ( SELECT TASK_NO , SUB_TASK_NO , SEND_DT , TASK_NM , CAMP_NO , CAMP_NM , SEG_NO , SEG_NM , USER_ID , USER_NM , STATUS , STATUS_NM , WORK_STATUS , WORK_STATUS_NM , SECU_ATT_YN , SVC_TYPE , SVC_TYPE_NM , REG_DT , ORG_KOR_NM , MKT_YN , ROW_NUMBER() OVER (ORDER BY TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT T1.TASK_NO , T2.SUB_TASK_NO , T2.SEND_DT , T1.TASK_NM , T1.CAMP_NO , T3.CAMP_NM , T1.SEG_NO , T4.SEG_NM , T1.USER_ID , T5.USER_NM , T1.STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T2.WORK_STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C101' AND CD = T2.WORK_STATUS) WORK_STATUS_NM , (SELECT SECU_ATT_YN FROM NEO_WEBAGENT WHERE TASK_NO = T1.TASK_NO) SECU_ATT_YN , '10' SVC_TYPE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T1.SEND_REPEAT) SVC_TYPE_NM , T1.REG_DT , T7.ORG_KOR_NM , (SELECT Z2.CD_NM FROM NEO_MAILMKT_CHK Z1,NEO_CD Z2 WHERE TASK_NO = T1.TASK_NO AND Z1.MAIL_MKT_GB = Z2.CD AND Z2.CD_GRP='C114' AND Z2.UILANG = #{uilang} ) AS MKT_YN FROM NEO_TASK T1 , NEO_SUBTASK T2 , NEO_CAMPAIGN T3 , NEO_SEGMENT T4 , NEO_USER T5 , NEO_USERORG_MAPP T6 , V_NEO_ORGANIZATION T7 WHERE T1.TASK_NO = T2.TASK_NO AND T1.CAMP_NO = T3.CAMP_NO AND T1.SEG_NO = T4.SEG_NO AND T1.USER_ID = T5.USER_ID AND T1.USER_ID = T6.USER_ID AND T6.ORG_CD = T7.ORG_CD AND EXISTS (SELECT 1 FROM NEO_SECUAPPROVAL_LINE LI, NEO_USER UR WHERE LI.APPR_USER_ID = UR.USER_ID AND LI.TASK_NO = T1.TASK_NO
AND LI.RSLT_CD = '001'
AND (UR.DEPT_NO = #{searchAprDeptNo} OR LI.REG_ID = #{regId})
AND UR.DEPT_NO = #{searchAprDeptNo}
AND UR.USER_ID = #{searchAprUserId}
) AND T2.SUB_TASK_NO = (SELECT MAX(SUB_TASK_NO) FROM NEO_SUBTASK WHERE TASK_NO = T1.TASK_NO) AND T2.SEND_TEST_YN = 'N' AND T2.WORK_STATUS NOT IN ('000', '201')
AND T2.SEND_DT >= #{searchStartDt} + '000000'
AND T2.SEND_DT < #{searchEndDt} + '240000'
AND T1.TASK_NM LIKE '%' + #{searchTaskNm} + '%'
AND T1.CAMP_NO = #{searchCampNo}
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.USER_ID = #{searchUserId}
AND T2.WORK_STATUS NOT IN ('000','001','002','003','201','202','203','204')
AND T2.WORK_STATUS = #{searchWorkStatus}
UNION ALL SELECT T1.TID TASK_NO , 0 SUB_TASK_NO , SUBSTRING( T1.REG_DT, 1, 12) SEND_DT , T1.EMAIL_SUBJECT TASK_NM , 0 CAMP_NO , T1.TNM CAMP_NM , 0 SEG_NO , '' SEG_NM , T1.USER_ID , T5.USER_NM , T1.STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T1.WORK_STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C101' AND CD = T1.WORK_STATUS) WORK_STATUS_NM , (SELECT SECU_ATT_YN FROM TS_WEBAGENT WHERE TID = T1.TID) SECU_ATT_YN , '20' SVC_TYPE , '실시간' SVC_TYPE_NM , T1.REG_DT , T7.ORG_KOR_NM , '' AS MKT_YN FROM TS_SERVICETYP T1 , NEO_USER T5 , NEO_USERORG_MAPP T6 , V_NEO_ORGANIZATION T7 WHERE T1.USER_ID = T5.USER_ID AND T1.USER_ID = T6.USER_ID AND T6.ORG_CD = T7.ORG_CD AND EXISTS (SELECT 1 FROM TS_SECUAPPROVAL_LINE LI , NEO_USER UR WHERE LI.APPR_USER_ID = UR.USER_ID AND LI.TID = T1.TID
AND LI.RSLT_CD = '001'
AND (UR.DEPT_NO = #{searchAprDeptNo} OR LI.REG_ID = #{regId})
AND UR.DEPT_NO = #{searchAprDeptNo}
AND UR.USER_ID = #{searchAprUserId}
) AND T1.WORK_STATUS NOT IN ('000', '201')
AND T1.REG_DT >= #{searchStartDt} + '000000'
AND T1.REG_DT < #{searchEndDt} + '240000'
AND T1.TNM LIKE '%'+ #{searchTaskNm} + '%'
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.USER_ID = #{searchUserId}
AND T1.WORK_STATUS NOT IN ('000','001','002','003','201','202','203','204')
AND T1.WORK_STATUS = #{searchWorkStatus}
) X ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT T1.TASK_NO , T2.SUB_TASK_NO , T2.SEND_DT , T1.TASK_NM , T1.CAMP_NO , T3.CAMP_NM , T1.SEG_NO , T4.SEG_NM , T1.USER_ID , T5.USER_NM , T1.STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T2.WORK_STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C101' AND CD = T2.WORK_STATUS) WORK_STATUS_NM , (SELECT SECU_ATT_YN FROM NEO_WEBAGENT WHERE TASK_NO = T1.TASK_NO) SECU_ATT_YN , '10' SVC_TYPE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T1.SEND_REPEAT) SVC_TYPE_NM , T1.REG_DT , T7.ORG_KOR_NM , (SELECT Z2.CD_NM FROM NEO_MAILMKT_CHK Z1,NEO_CD Z2 WHERE TASK_NO = T1.TASK_NO AND Z1.MAIL_MKT_GB = Z2.CD AND Z2.CD_GRP='C114' AND Z2.UILANG = #{uilang}) AS MKT_YN , ROW_NUMBER() OVER (ORDER BY T1.TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_TASK T1 , NEO_SUBTASK T2 , NEO_CAMPAIGN T3 , NEO_SEGMENT T4 , NEO_USER T5 , NEO_USERORG_MAPP T6 , V_NEO_ORGANIZATION T7 WHERE T1.TASK_NO = T2.TASK_NO AND T1.CAMP_NO = T3.CAMP_NO AND T1.SEG_NO = T4.SEG_NO AND T1.USER_ID = T5.USER_ID AND T1.USER_ID = T6.USER_ID AND T6.ORG_CD = T7.ORG_CD AND EXISTS (SELECT 1 FROM NEO_SECUAPPROVAL_LINE LI , NEO_USER UR WHERE LI.APPR_USER_ID = UR.USER_ID AND LI.TASK_NO = T1.TASK_NO
AND LI.RSLT_CD = '001'
