mapper.update:
UPDATE NEO_SECUAPPROVAL_LINE
   SET RSLT_CD   = #{rsltCd}
	 , REJECT_CD = #{rejectCd}
	 , APPR_DT	 = #{apprDt}
	 , UP_ID     = #{upId}
	 , UP_DT     = #{upDt}
 WHERE TASK_NO   = #{taskNo}
   AND APPR_STEP = #{apprStep}

UPDATE NEO_SUBTASK
   SET WORK_STATUS = #{workStatus}
 WHERE TASK_NO     = #{taskNo}
   AND SUB_TASK_NO = #{subTaskNo}

UPDATE NEO_TASK
   SET RECO_STATUS = #{recoStatus}
	 , EXE_USER_ID = #{exeUserId}
 WHERE TASK_NO     = #{taskNo}

UPDATE NEO_SUBTASK
   SET WORK_STATUS = #{workStatus}
 WHERE TASK_NO     = #{taskNo}
   AND SUB_TASK_NO = #{subTaskNo}

mapper.select:
SELECT CD_NM
  FROM NEO_CD
 WHERE CD_GRP = 'C104'
   AND UILANG = #{uilang}
   AND CD     = #{rejectCd}
