mapper.select:
SELECT ISNULL(A.PW_INIT_YN, 'N') PW_INIT_YN
  FROM NEO_USER A
 WHERE A.USER_ID = #{pUserId}

mapper.update:
UPDATE NEO_USER
   SET LSTACCESS_DT = #{lstaccessDt}
	 , LSTACCESS_IP = #{lstaccessIp}
 	 , PWERROR_CNT = 0
 WHERE USER_ID	   = #{userId}

UPDATE NEO_USER
   SET PWERROR_CNT = IFNULL(PWERROR_CNT, 0) + 1
 WHERE USER_ID    = #{userId}
