mapper.select:
SELECT MENU_ID, MENU_NM, PARENTMENU_ID
  FROM NEO_SYSMENU
 WHERE SOURCE_PATH = #{sourcePath}

SELECT T2.MENU_ID, T2.MENU_NM, T2.PARENTMENU_ID
  FROM NEO_MENUUSER_MAPP T1
	 , NEO_SYSMENU T2
 WHERE T1.MENU_ID = T2.MENU_ID
   AND T1.USER_ID = #{userId}
   AND T2.SOURCE_PATH = #{sourcePath}

mapper.update:
UPDATE NEO_MENUUSER_MAPP 
   SET QMENU_YN ='N' 
 WHERE MENU_ID = #{qmenuYn}
   AND USER_ID = #{userId}

UPDATE NEO_MENUUSER_MAPP
   SET QMENU_YN ='N'
 WHERE USER_ID = #{userId}

UPDATE NEO_MENUUSER_MAPP 
   SET QMENU_YN ='Y' 
 WHERE USER_ID = #{userId} 
   AND MENU_ID IN 
 
 #{item}
