mapper.select:
SELECT * FROM ( SELECT A.CAMP_NO , A.CAMP_NM , A.USER_ID , A.CAMP_TY , A.STATUS , A.DEPT_NO , A.REG_DT , A.REG_ID , A.UP_ID , A.UP_DT , A.CAMP_DESC , B.USER_NM , C.USER_NM REG_NM , D.DEPT_NM , E.CD_NM CAMP_TY_NM , F.CD_NM STATUS_NM , (SELECT COUNT(1) FROM NEO_PUSHMESSAGE T1 WHERE T1.STATUS = '000' AND T1.CAMP_NO = A.CAMP_NO) CAMP_PUSH_CNT , ROW_NUMBER() OVER (ORDER BY A.CAMP_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_CAMPAIGN A , NEO_USER B , NEO_USER C , NEO_DEPT D , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C004' AND UILANG = #{uilang}) E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C014' AND UILANG = #{uilang}) F WHERE A.USER_ID = B.USER_ID AND A.REG_ID = C.USER_ID AND A.DEPT_NO = D.DEPT_NO AND A.CAMP_TY = E.CD AND A.STATUS = F.CD
AND A.CAMP_NM LIKE '%' + #{searchCampNm}+ '%'
AND A.CAMP_TY = #{searchCampTy}
AND A.STATUS = #{searchStatus}
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.REG_DT >= #{searchStartDt} + '000000'
AND A.REG_DT < #{searchEndDt} + '240000'
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_CAMPAIGN
(
	  CAMP_NM
	, CAMP_DESC
	, USER_ID
	, CAMP_TY
	, STATUS
	, DEPT_NO
	, REG_DT
	, REG_ID
	, UP_ID
	, UP_DT
	, EAI_CAMP_NO
)
VALUES
(
	  #{campNm}
	, #{campDesc}
	, #{userId}
	, #{campTy}
	, #{status}
	, #{deptNo}
	, #{regDt}
	, #{regId}
	, NULL
	, NULL
	, #{eaiCampNo}
)

mapper.update:
UPDATE NEO_CAMPAIGN
   SET CAMP_NM     = #{campNm}
	 , CAMP_DESC   = #{campDesc}
	 , USER_ID     = #{userId}
	 , CAMP_TY     = #{campTy}
	 , STATUS      = #{status}
	 , DEPT_NO     = #{deptNo}
	 , UP_ID       = #{upId}
	 , UP_DT       = #{upDt}
	 , EAI_CAMP_NO = #{eaiCampNo}
 WHERE CAMP_NO     = #{campNo}

mapper.select:
SELECT * FROM ( SELECT A.PUSHMESSAGE_ID , A.USER_ID , (SELECT USER_NM FROM NEO_USER WHERE USER_ID = A.USER_ID) USER_NM , A.DEPT_NO , B.DEPT_NM , A.SEG_NO , ISNULL(C.SEG_NM, '' ) SEG_NM , A.SEND_TELNO , A.CAMP_NO CAMP_NO , D.CAMP_NM , A.SEND_DT , A.PUSH_NAME , A.PUSH_MESSAGE , A.STATUS , E.CD_NM STATUS_NM , A.PUSH_GUBUN PUSH_GUBUN , F.CD_NM PUSH_GUBUN_NM , A.WORK_STATUS WORK_STATUS , G.CD_NM WORK_STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_PUSHMESSAGE A INNER JOIN NEO_DEPT B ON (A.DEPT_NO = B.DEPT_NO) LEFT OUTER JOIN NEO_SEGMENT C ON (A.SEG_NO = C.SEG_NO) LEFT OUTER JOIN NEO_CAMPAIGN D ON (A.CAMP_NO = D.CAMP_NO) INNER JOIN (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C009' AND UILANG = #{uilang}) E ON (A.STATUS = E.CD) INNER JOIN (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C124' AND UILANG = #{uilang}) F ON (A.PUSH_GUBUN = F.CD) INNER JOIN (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C118' AND UILANG = #{uilang}) G ON (A.WORK_STATUS = G.CD) WHERE 1=1
AND A.SEND_DT >= #{searchStartDt} + '000000'
AND A.SEND_DT < #{searchEndDt} + '240000'
AND A.PUSH_NAME LIKE '%' + #{searchPushName} + '%'
AND A.PUSH_GUBUN = #{searchPushGubun}
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.STATUS = #{searchStatus}
AND A.WORK_STATUS = #{searchWorkStatus}
AND A.CAMP_NO = #{searchCampNo}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

