mapper.update:
UPDATE NEO_PUSHMESSAGE
   SET STATUS = #{status}
 WHERE PUSHMESSAGE_ID = #{pushmessageId}

UPDATE NEO_PUSHMESSAGE
   SET WORK_STATUS = #{workStatus}
	 , EXE_USER_ID = #{upId}
 WHERE PUSHMESSAGE_ID = #{pushmessageId}

UPDATE NEO_PUSHMESSAGE
   SET USER_ID        = #{userId}
	 , DEPT_NO        = #{deptNo}
	 , UP_ID          = #{upId}
	 , UP_DT          = #{upDt}
	 , SEG_NO         = #{segNo}
	 , SEND_TELNO     = #{sendTelno}
	 , CAMP_NO        = #{campNo}
	 , PUSH_NAME      = #{pushName}
	 , PUSH_MESSAGE   = #{pushMessage}
	 , PUSH_GUBUN     = #{pushGubun}
	 , FILE_PATH      = #{filePath}
