mapper.select:
SELECT CONVERT(NUMERIC, (SUBSTRING(CONVERT(VARCHAR(8), A.SDATE, 112),7,2))) DAYS , SUM(B.SCOUNT) SEND , SUM(B.ECODE01) SUCCESS , SUM(B.ECODE02+B.ECODE03+B.ECODE04+B.ECODE05+B.ECODE06+B.ECODE07+B.ECODE08+B.ECODE09+B.ECODE10+B.ECODE11+B.ECODE12+B.ECODE13+ B.ECODE14+B.ECODE15+B.ECODE16+B.ECODE17+B.ECODE18+B.ECODE19+B.ECODE20+B.ECODE21+B.ECODE41+B.ECODE42) FAILED , SUM(C.RSCOUNT) OPENED FROM TS_MAILQUEUE A , TS_MAILQUEUE_RSINFO B , TS_RESPONSE_RSINFO C WHERE B.MID = C.MID AND A.MID = B.MID AND A.MID = C.MID AND B.MID IN (SELECT MID FROM TS_MAILQUEUE WHERE CONVERT(CHAR(6),SDATE,112)= #{searchYm})
AND B.TID = #{searchService}
AND A.SNAME = #{searchSname}
GROUP BY CONVERT(VARCHAR(8),A.SDATE,112) ORDER BY CONVERT(VARCHAR(8),A.SDATE,112)

SELECT B.TID , D.TNM , SUM(B.SCOUNT) SEND , SUM(B.ECODE01) SUCCESS , SUM(B.ECODE02+B.ECODE03+B.ECODE04+B.ECODE05+B.ECODE06+B.ECODE07+B.ECODE08 +B.ECODE09+B.ECODE10+B.ECODE11+B.ECODE12+B.ECODE13+B.ECODE14+B.ECODE15+B.ECODE16 +B.ECODE17+B.ECODE18+B.ECODE19+B.ECODE20+B.ECODE21+B.ECODE41+B.ECODE42) FAILED , SUM(C.RSCOUNT) REPLY FROM TS_MAILQUEUE A , TS_MAILQUEUE_RSINFO B , TS_RESPONSE_RSINFO C , TS_SERVICETYP D WHERE B.MID = C.MID AND A.MID = B.MID AND A.MID = C.MID AND B.TID = D.TID AND CONVERT(VARCHAR(8),A.SDATE,112) >= #{searchStdDt} AND CONVERT(VARCHAR(8),A.SDATE,112) <= #{searchEndDt}
AND A.SNAME = #{searchSname}
GROUP BY B.TID, D.TNM ORDER BY B.TID

SELECT * FROM ( SELECT X.MID , X.SUBID , X.TID , Y.SUBJECT , Y.SMAIL , X.RMAIL , X.RID , X.RNAME , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG =#{uilang} AND X.RCODE = RCODE ) RCODE , Y.CONTENTS , C.ATTCH_CNT , Y.SDATE , X.STIME , REPLACE(B.RSDATE, '/', '-') RSDATE , C.TNM , Y.SNAME , ISNULL((SELECT DEPT_NM FROM NEO_DEPT WHERE DEPT_NO = C.DEPT_NO ), '정보없음') DEPT_NM , ROW_NUMBER() OVER (ORDER BY Y.SDATE DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM TS_RESULTLOG X LEFT OUTER JOIN TS_RESPONSELOG B ON X.MID = B.MID AND X.MID = B.RID , TS_MAILQUEUE Y , TS_SERVICETYP C , ( SELECT MID, MAX(SUBID) SUBID, RID FROM TS_RESULTLOG GROUP BY MID, RID) D WHERE X.MID = Y.MID AND X.TID = Y.TID AND X.SUBID = Y.SUBID AND X.TID = C.TID AND X.MID = D.MID AND X.SUBID = D.SUBID AND X.RID = D.RID
AND CONVERT(NUMERIC, X.RCODE) = CONVERT(NUMERIC, #{searchRcode})
AND CONVERT(VARCHAR(8), Y.SDATE, 112) >= #{searchStdDt}
AND CONVERT(VARCHAR(8), Y.SDATE, 112) <= #{searchEndDt}
AND X.RNAME LIKE '%' + #{searchRname} + '%'
AND X.RMAIL = #{searchRmail}
AND X.RID LIKE '%' + #{searchRid} + '%'
AND X.TID = #{searchService}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT Y.MID , Y.SUBID , Y.TID , Y.SUBJECT , Y.SMAIL , Y.CDATE , Y.SDATE , C.TNM , Y.REFMID , Y.STATUS , Y.CONTENTS , Y.SNAME , C.ATTCH_CNT , ISNULL((SELECT DEPT_NM FROM NEO_DEPT WHERE DEPT_NO = C.DEPT_NO ), '정보없음') DEPT_NM FROM TS_MAILQUEUE Y , TS_SERVICETYP C WHERE Y.TID = C.TID
AND CONVERT(VARCHAR,Y.SDATE,112) >= #{searchStdDt}
AND CONVERT(VARCHAR,Y.SDATE,112) <= #{searchEndDt}
AND Y.TID = #{searchService}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT SDATE , CDATE , MID , SUBID , TID , TNM , SUBJECT , RID , RNAME , RMAIL , SID , SNAME , SMAIL , STATUS , REFMID , DEPT_NM , CONTENTS , ATTCH_CNT , RCODE , ROW_NUMBER() OVER (ORDER BY SDATE DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT Y.SDATE , Y.CDATE , X.MID , D.SUBID , X.TID , C.TNM , Y.SUBJECT , X.RID , X.RNAME , X.RMAIL , X.SID , X.SNAME , X.SMAIL , Y.STATUS , X.REFMID , ISNULL((SELECT DEPT_NM FROM NEO_DEPT WHERE DEPT_NO = C.DEPT_NO ), '정보없음') DEPT_NM , Y.CONTENTS , C.ATTCH_CNT , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG =#{uilang} AND X.RCODE = RCODE ) RCODE FROM TS_RESULTLOG X , TS_SERVICETYP C , TS_MAILQUEUE Y , (SELECT MID, MAX(SUBID) SUBID FROM TS_MAILQUEUE GROUP BY MID) D WHERE X.TID = C.TID AND X.MID = Y.MID AND X.MID = D.MID AND X.SUBID= Y.SUBID AND X.TID = Y.TID
AND CONVERT(VARCHAR, Y.SDATE, 112) >= #{searchStdDt}
AND CONVERT(VARCHAR, Y.SDATE, 112) <= #{searchEndDt}
AND X.TID = #{searchService}
AND X.SNAME = #{searchSname}
AND CONVERT(NUMERIC, X.RCODE) = CONVERT(NUMERIC, #{searchRcode})
AND X.MID = #{searchMid}
) RR WHERE NOT EXISTS ( SELECT MID FROM TS_MAILQUEUE_TEST RT WHERE RR.MID = RT.MID ) ) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}
