mapper.select:
SELECT * FROM ( SELECT T1.TID , T1.TNM , T1.DEPT_NO , T2.DEPT_NM , T1.USER_ID , T3.USER_NM , T1.CONTENTS_TYP , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C109' AND CD = T1.CONTENTS_TYP) CONTENTS_TYP_NM , T1.STATUS , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T1.REG_DT , T1.WORK_STATUS , T6.CD_NM WORK_STATUS_NM , T6.CD_DTL WORK_STATUS_DTL , CASE WHEN (SELECT COUNT(1) FROM TS_SECUAPPROVAL_LINE WHERE TID = T1.TID) = 0 THEN 'N' ELSE 'Y' END APPROVAL_LINE_YN , CASE WHEN (SELECT COUNT(1) FROM TS_SECUAPPROVAL_LINE WHERE TID = T1.TID AND RSLT_CD >= '002') = 0 THEN 'N' ELSE 'Y' END APPROVAL_PROC_YN , CASE WHEN (SELECT COUNT(1) FROM TS_SECUAPPROVAL_LINE WHERE TID = T1.TID AND RSLT_CD = '002') = 0 THEN 'N' ELSE 'Y' END APPROVAL_PROC_APP_YN , ISNULL(T1.EAI_CAMP_NO, '') EAI_CAMP_NO , ROW_NUMBER() OVER (ORDER BY T1.REG_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM TS_SERVICETYP T1, NEO_DEPT T2, NEO_USER T3, NEO_CD T6 WHERE T1.DEPT_NO = T2.DEPT_NO AND T1.USER_ID = T3.USER_ID AND T1.WORK_STATUS = T6.CD AND T6.UILANG = #{uilang} AND T6.CD_GRP = 'C121'
AND T1.REG_DT >= #{searchStartDt} + '000000'
AND T1.REG_DT < #{searchEndDt} + '240000'
AND T1.TNM LIKE '%' + #{searchTnm} + '%'
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.USER_ID = #{searchUserId}
AND T1.CONTENTS_TYP = #{searchContentsTyp}
AND T1.STATUS = #{searchStatus}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO TS_SERVICETYP
(
	  TID
	, TNM
	, SNAME
	, SMAIL
	, SID
	, EMAIL_SUBJECT
	, CONTENTS_PATH
	, CONTENTS_TYP
	, ATTCH_CNT
	, RECV_CHK_YN
	, USER_ID
	, DEPT_NO
	, USE_YN
	, STATUS
	, EAI_CAMP_NO
	, REG_ID
	, REG_DT
	, WORK_STATUS
)
VALUES
(
	  NEXT VALUE FOR TS_SERVICE_TID_SEQ
	, #{tnm}
	, #{sname}
	, #{smail}
	, #{sid}
	, #{emailSubject}
	, #{contentsPath}
	, #{contentsTyp}
	, #{attchCnt}
	, #{recvChkYn}
	, #{userId}
	, #{deptNo}
	, #{useYn}
	, #{status}
	, #{eaiCampNo}
	, #{regId}
	, #{regDt}
	, #{workStatus}
)

mapper.update:
UPDATE TS_SERVICETYP
   SET
	  TNM           = #{tnm}
	, SNAME         = #{sname}
	, SMAIL         = #{smail}
	, SID           = #{sid}
	, EMAIL_SUBJECT = #{emailSubject}
	, CONTENTS_PATH = #{contentsPath}
	, CONTENTS_TYP  = #{contentsTyp}
	, ATTCH_CNT     = #{attchCnt}
	, RECV_CHK_YN   = #{recvChkYn}
	, USER_ID       = #{userId}
	, DEPT_NO       = #{deptNo}
	, USE_YN        = #{useYn}
	, EAI_CAMP_NO   = #{eaiCampNo}
	, UP_ID         = #{upId}
	, UP_DT         = #{upDt}
	, WORK_STATUS   = #{workStatus}
WHERE TID           = #{tid}

UPDATE TS_SERVICETYP
   SET STATUS = #{status}
	 , UP_ID  = #{upId}
	 , UP_DT  = #{upDt}
 WHERE TID    = #{tid}

