mapper.select:
SELECT * FROM ( SELECT T1.MID , T2.TID , T2.SDATE , T3.TNM , T1.REG_ID , T4.USER_NM REG_NM , (SELECT COUNT(1) FROM TS_RESULTLOG WHERE MID = T2.MID AND TID = T2.TID AND RCODE = '1') SUCC_CNT , (SELECT COUNT(1) FROM TS_RESULTLOG WHERE MID = T2.MID AND TID = T2.TID AND RCODE != '1') FAIL_CNT , ROW_NUMBER() OVER (ORDER BY T2.SDATE DESC, T2.TID) SEQ , COUNT(1) OVER () TOTAL_COUNT FROM TS_MAILQUEUE_TEST T1 , TS_MAILQUEUE T2 , TS_SERVICETYP T3 , NEO_USER T4 WHERE T1.MID = T2.MID AND T2.TID = T3.TID AND T1.REG_ID = T4.USER_ID AND T2.SUBID = 0
AND T3.DEPT_NO = #{searchDeptNo}
AND CONVERT(VARCHAR, T2.SDATE , 120 ) >= CONVERT(VARCHAR, #{searchStartDt}+'000000' , 120)
AND CONVERT(VARCHAR, T2.SDATE , 120 ) <= CONVERT(VARCHAR, #{searchEndDt}+'235959' , 120)
AND T3.TNM LIKE '%' + #{searchTnm} + '%'
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

