	, #{positionGb}
	, #{jobGb}
	, #{apprDt}
	, #{rsltCd}
	, #{rejectCd}
	, #{regId}
	, #{regDt}
)

mapper.update:
UPDATE TS_SECUAPPROVAL_LINE
   SET
	   APPR_DT  = #{apprDt}
	 , RSLT_CD = #{rsltCd} 
	 , UP_ID   = #{upId}
	 , UP_DT   = #{upDt}
 WHERE TID     = #{tid}

UPDATE TS_SECUAPPROVAL_LINE
   SET RSLT_CD   = #{rsltCd}
	 , REJECT_CD = #{rejectCd}
	 , APPR_DT   = #{apprDt}
	 , UP_ID     = #{upId}
	 , UP_DT     = #{upDt}
 WHERE TID       = #{tid}
   AND APPR_STEP = #{apprStep}

UPDATE TS_SERVICETYP
   SET WORK_STATUS = #{workStatus}
 WHERE TID         = #{tid}

UPDATE TS_SERVICETYP
   SET WORK_STATUS = #{workStatus}
 WHERE TID         = #{tid}

UPDATE TS_SECUAPPROVAL_LINE
   SET RSLT_CD   = '001'
	 , UP_ID     = #{upId}
	 , UP_DT     = #{upDt}
 WHERE TID       = #{tid}
   AND APPR_STEP = 1
