mapper.select:
SELECT * FROM ( SELECT B.DEPT_NO , A.DEPT_NM , B.USER_ID , B.LGN_HST_ID , B.LGN_DT , B.LGN_IP , ROW_NUMBER() OVER (ORDER BY LGN_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_DEPT A , NEO_LGN_HST B WHERE A.DEPT_NO = B.DEPT_NO AND LGN_DT >= #{searchLgnStdDt} + '000000' AND LGN_DT < #{searchLgnEndDt} + '240000'
AND A.DEPT_NM LIKE '%' + #{searchDeptNm} + '%'
AND B.USER_ID LIKE '%' + #{searchUserId} + '%'
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT A.LOG_DT , A.SESSION_ID , A.IP_ADDR , A.USER_ID , A.DEPT_NO , A.ORG_CD , A.STATUS_GB , A.CONTENT_TYPE , IIF(F.MENU_ID is NULL ,'/' + F.SERVICE_NM + '/' + F.P_MENU_NM + '/' + F.MENU_NM , A.CONTENT) CONTENT , A.CONTENT_PATH , A.CONTENT_RSLT , (CASE WHEN A.CONTENT_TYPE ='004' THEN (SELECT CD_NM + '(' + A.CONTENT_RSLT + ')' FROM NEO_CD WHERE CD_GRP = 'C103' AND USE_YN = 'Y' AND UILANG = #{uilang} AND CD = A.CONTENT_RSLT ) ELSE A.MESSAGE END) MESSAGE , A.EXTR_YN , A.REC_CNT , A.MOBIL_YN , B.USER_NM , B.DEPT_NM , D.ORG_KOR_NM ORG_NM , E.CD_NM CONTENT_TYPE_NM , ROW_NUMBER() OVER (ORDER BY LOG_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_ACTION_LOG A INNER JOIN ( SELECT USER_ID, USER_NM, DEPT_NM FROM NEO_USER X, NEO_DEPT Y WHERE X.DEPT_NO = Y.DEPT_NO ) B ON ( A.USER_ID = B.USER_ID) INNER JOIN ( SELECT X.USER_ID, X.ORG_CD, Y.ORG_KOR_NM FROM NEO_USERORG_MAPP X, V_NEO_ORGANIZATION Y WHERE X.ORG_CD = Y.ORG_CD
AND Y.ORG_CD IN
#{item}
) D ON (A.USER_ID = D.USER_ID) INNER JOIN ( SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C112' AND USE_YN = 'Y' AND UILANG = #{uilang}) E ON (A.CONTENT_TYPE = E.CD) LEFT OUTER JOIN ( SELECT P.MENU_ID, R.SERVICE_NM, Q.MENU_NM P_MENU_NM, P.MENU_NM FROM NEO_SYSMENU P, NEO_SYSMENU Q, (SELECT SERVICE_GB, SERVICE_NM FROM NEO_SERVICEKEY UNION ALL SELECT 99 SERVICE_GB, '공통설정' SERVICE_NM) R WHERE P.FUNCTION_YN = 'Y' AND P.PARENTMENU_ID = Q.MENU_ID AND P.SERVICE_GB = R.SERVICE_GB) F ON (CONTENT = F.MENU_ID) WHERE A.LOG_DT >= #{searchLogStdDt} + '000000' AND A.LOG_DT < #{searchLogEndDt} + '240000'
AND A.STATUS_GB = #{searchStatusGb}
AND A.CONTENT_TYPE = #{searchContentType}
AND A.DEPT_NO = #{searchDeptNo}
AND B.USER_NM LIKE '%' + #{searchUserNm} + '%'
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

