mapper.select:
SELECT * FROM ( SELECT T1.TEMP_NM , T1.TEMP_CD , T1.TEMP_CONTENT , T1.MERGY_ITEM , T2.DEPT_NM , T3.USER_NM , T1.REG_DT , T1.STATUS , (SELECT CD_NM FROM NEO_CD WHERE CD_GRP = 'C023' AND CD= T1.STATUS AND UILANG =#{uilang}) STATUS_NM , ROW_NUMBER() OVER (ORDER BY T1.REG_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_KAKAOTALK_TEMPLATE T1 , NEO_DEPT T2 , NEO_USER T3 WHERE T1.DEPT_NO = T2.DEPT_NO AND T1.USER_ID = T3.USER_ID
AND T1.REG_DT >= #{searchStartDt} + '000000'
AND T1.REG_DT < #{searchEndDt} + '240000'
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.STATUS = #{searchStatus}
AND T1.TEMP_NM LIKE '%' + #{searchTempNm} + '%'
AND T1.TEMP_CD LIKE '%' + #{searchTempCd} + '%'
AND T1.USER_ID = #{searchUserId}
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.update:
UPDATE NEO_KAKAOTALK_TEMPLATE 
   SET TEMP_NM           = #{tempNm}
	 , TEMP_DESC         = #{tempDesc}
	 , TEMP_CONTENT      = #{tempContent}
	 , MERGY_ITEM        = #{mergyItem}
	 , STATUS            = #{status}
	 , DEPT_NO           = #{deptNo}
	 , USER_ID           = #{userId}
	 , UP_ID             = #{upId}
	 , UP_DT             = #{upDt}
	 , LEGAL_YN          = #{legalYn}
	 , WEBLINK_YN        = #{weblinkYn}
	 , SEG_NO            = #{segNo}
	 , TEMP_MAPP_CONTENT = #{tempMappContent}
	 , MAPP_TEMP_CD      = #{apiTempCd}
 WHERE TEMP_CD = #{tempCd}

mapper.insert:
INSERT INTO NEO_KAKAOTALK_TEMPLATE
