  , #{tempContent}
  , #{mergyItem}
  , #{status}
  , #{deptNo}
  , #{userId}
  , #{regId}
  , #{regDt}
  , #{legalYn}
  , #{weblinkYn}
  , #{segNo}
  , #{tempMappContent}
  , #{apiTempCd}
)

mapper.select:
SELECT COUNT(*) AS COUNT
  FROM NEO_KAKAOTALK_TEMPLATE
 WHERE TEMP_CD = #{tempCd}

mapper.insert:
