mapper.select:
SELECT * FROM ( SELECT SEG_NO , DB_CONN_NO , SEG_NM , SEG_DESC , CREATE_TY_CD.CD_NM CREATE_TY_NM , CREATE_TY , SELECT_ SELECT_SQL , FROM_ FROM_SQL , WHERE_ WHERE_SQL , ORDERBY_ ORDERBY_SQL , QUERY , MERGE_KEY , MERGE_COL , SEG_FL_PATH , SRC_WHERE , TOT_CNT , NEO_SEGMENT.STATUS , STATUS_CD.CD_NM STATUS_NM , NEO_SEGMENT.USER_ID , NEO_USER.USER_NM , NEO_DEPT.DEPT_NM , NEO_SEGMENT.REG_DT , ROW_NUMBER() OVER (ORDER BY NEO_SEGMENT.REG_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SEGMENT , NEO_CD CREATE_TY_CD , NEO_CD STATUS_CD , NEO_USER , NEO_DEPT WHERE CREATE_TY_CD.CD_GRP = 'C013' AND CREATE_TY_CD.UILANG = #{uilang} AND STATUS_CD.CD_GRP = 'C012' AND STATUS_CD.UILANG = #{uilang} AND NEO_SEGMENT.CREATE_TY = CREATE_TY_CD.CD AND NEO_SEGMENT.STATUS = STATUS_CD.CD AND NEO_SEGMENT.USER_ID = NEO_USER.USER_ID AND NEO_SEGMENT.DEPT_NO = NEO_DEPT.DEPT_NO
AND NEO_SEGMENT.SEG_NM LIKE '%' + #{searchSegNm} + '%'
AND NEO_SEGMENT.DEPT_NO = #{searchDeptNo}
AND NEO_SEGMENT.USER_ID = #{searchUserId}
AND NEO_SEGMENT.CREATE_TY = #{searchCreateTy}
AND NEO_SEGMENT.STATUS = #{searchStatus}
AND NEO_SEGMENT.REG_DT >= #{searchStartDt} + '000000'
AND NEO_SEGMENT.REG_DT < #{searchEndDt} + '240000'
AND (1 = 2
OR NEO_SEGMENT.EMSUSE_YN = 'Y'
OR NEO_SEGMENT.SMSUSE_YN = 'Y'
OR NEO_SEGMENT.PUSHUSE_YN = 'Y'
)
) SRC WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

