mapper.update:
UPDATE NEO_SEGMENT
   SET USER_ID     = #{userId}
	 , DB_CONN_NO  = #{dbConnNo}
	 , SEG_NM      = #{segNm}
	 , SEG_DESC    = #{segDesc}
	 , CREATE_TY   = #{createTy}
	 , SELECT_     = #{selectSql}
	 , FROM_       = #{fromSql}
	 , WHERE_      = #{whereSql}
	 , ORDERBY_    = #{orderbySql}
	 , QUERY       = #{query}
	 , SEPARATOR_  = #{separatorChar}
	 , MERGE_KEY   = #{mergeKey}
	 , MERGE_COL   = #{mergeCol}
	 , SEG_FL_PATH = #{segFlPath}
	 , SRC_WHERE   = #{srcWhere}
	 , TOT_CNT     = #{totCnt}
	 , UP_ID       = #{upId}
	 , UP_DT       = #{upDt}
	 , STATUS      = #{status}
	 , EMSUSE_YN   = #{emsuseYn}
	 , SMSUSE_YN   = #{smsuseYn}
	 , PUSHUSE_YN  = #{pushuseYn}
	 , DEPT_NO     = #{deptNo}
 WHERE SEG_NO      = #{segNo}

UPDATE NEO_SEGMENT
   SET STATUS = #{status}
	 , UP_ID  = #{upId}
	 , UP_DT  = #{upDt}
 WHERE SEG_NO = #{segNo}
